import 'package:arabheritage/horses.dart';
import 'package:arabheritage/details.dart';
import 'package:flutter/material.dart';

void main() {
  runApp(MainApp());
}

class MainApp extends StatefulWidget {
  MainApp({super.key});

  @override
  State<MainApp> createState() => _MainAppState();
}

class _MainAppState extends State<MainApp> {
  var buildings = [
    horse(
      name: 'هندسه اسطبلات الخيل',
      image:
          'data:image/jpeg;base64,',
    ),
    horse(
      name: 'العرب و الخيل',
      image:
          'data:image/jpeg;base64,',
    ),
    horse(
      name: 'طريقه عسف الخيول',
      image:
          'data:image/jpeg;base64,',
    ),
    horse(
      name: " evidence_from_sunnah_and_quran",
      image:
          'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR1r0eMIPJRH2O6B9B5K1V68sbdtZr5iuIMZw&usqp=CAU',
    ),
  ];
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      debugShowCheckedModeBanner: false,
      home: Scaffold(
        appBar: AppBar(
          title: Text('الخيل العربي الاصيل'),
          backgroundColor: Colors.brown,
        ),
        body: Center(
            child: ListView.builder(
                itemCount: buildings.length,
                itemBuilder: (context, index) {
                  return ListTile(
                    leading: Image.network(buildings[index].image),
                    title: Text(buildings[index].name),
                    trailing: IconButton(
                        onPressed: () {
                          Navigator.push(
                              context,
                              MaterialPageRoute(
                                  builder: (context) =>
                                      DetailsPage(flutter: buildings[index])));
                        },
                        icon: Icon(Icons.arrow_back)),
                  );
                })),
      ),
    );
  }
}
